    string serialize(TreeNode* root) {
        //#
        string res;
        if (root == nullptr)
        {
            return res.append("#"); // 
        }

        queue<TreeNode*> q;
        q.push(root); // 
        while (!q.empty()) 
        {
            TreeNode* current = q.front(); // 
            res.append(to_string(current->val));

            q.pop(); // 

            // 
            if (current->left != nullptr) 
            {
                q.push(current->left);
            }
            else
            {
                res.append("#"); 
            }
            // 
