class Solution {
public:
    int numberOfBoomerangs(vector<vector<int>>& points) {
        int n = points.size();
        int ans = 0;

        unordered_map<int, int> cnt;

        for (int i = 0; i < n; i++) {
            cnt.clear();
            for (int j = 0; j < n; j++) {
                if (i == j)
                    continue;
                int x = (points[i][0] - points[j][0]) *
                        (points[i][0] - points[j][0]);
                int y = (points[i][1] - points[j][1]) *
                        (points[i][1] - points[j][1]);
                cnt[x + y]++;
            }
            for (auto p : cnt) {
                ans += p.second * (p.second - 1);
            }
        }

        return ans;
    }
};

