class Solution {
public:
  int minSubarray(vector<int>& nums, int p) {
    unordered_map<int, int> map;
    int deal = 0;
    long sum = 0;
    for (int i = 0; i < nums.size(); ++i) {
      deal = (deal + nums[i]) % p;
      sum += nums[i];
      map[nums[i]]++;
    }

    if (sum < p) {
      return -1;
    }

    if(deal == 0)
    {
      return 0;
    }
    int ret = minKeyCount(map, deal);

    return ret;
  }

  int minKeyCount(unordered_map<int, int>& hashTable, int target) {
    if(hashTable.size()==1)
    {
      if(hashTable.find(target)!= hashTable.end())
      {
        return 1;
      }
      else
      {
        return -1;
      }
    }

    vector<int> dp(target + 1, INT_MAX);
    dp[0] = 0;

    for (const auto& kv : hashTable) {
      int key = kv.first;
      int value = kv.second;

      for (int i = key; i <= target; ++i) {
        if (dp[i - key] != INT_MAX) {
          dp[i] = min(dp[i], dp[i - key] + value);
        }
      }
    }

    return dp[target] == INT_MAX ? -1 : dp[target];
  }
};

