class Solution {
public:
    vector<int> findAnagrams(string s, string p) {
        int left = 0;
        int right = 0;
        vector<int> ans;
        unordered_map<char, int> window;
        unordered_map<char, int> need;
        for (char c : p) need[c]++;
        int cnt = 0;
        while (right < s.size()) {
            char r_char = s[right];
            if (need.count(r_char)) {
                window[r_char]++;
                if (window[r_char] == need[r_char]) {
                    cnt++;
                }
            }
            if (right - left + 1 > p.size()) {
                char l_char = s[left];
                if (need.count(l_char)) {
                    if (window[l_char] == need[l_char]) {
                        cnt--;
                    }
                    window[l_char]--;
                }
                left++;
            }
            if (cnt == need.size()) {
                ans.push_back(left);
            }
            right++;
        }
        return ans;
    }
};
