class Solution {
public:
    typedef pair<int, int> PII;
    int dx[4] = {-1, 1, 0, 0}, dy[4] = {0, 0, -1, 1};
    int bfs(vector<vector<int>>& grid, vector<vector<bool>>& st, int x, int y) {
        int area = 1;
        queue<PII> q;
        q.push({x, y});
        while (!q.empty()) {
            auto t = q.front();
            q.pop();

            for (int i = 0; i < 4; i++) {
                int xi = t.first + dx[i];
                int yi = t.second + dy[i];

                if (xi < 0 || xi >= grid.size() || yi < 0 ||
                    yi >= grid[0].size()) {
                    continue;
                }

                if (!st[xi][yi] && grid[xi][yi] == 1) {
                    area++;
                    st[xi][yi] = true;
                    q.push({xi, yi});
                }
            }
        }
        return area;
    }
    int maxAreaOfIsland(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();

        vector<vector<bool>> st =
            vector<vector<bool>>(n, vector<bool>(m, false));

        int cnt = 0;
        int max_area = 0;

        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                if (!st[i][j] && grid[i][j] == 1) {
                    st[i][j] = true;
                    cnt++;
                    int area = bfs(grid, st, i, j);
                    max_area = max(max_area, area);
                }
            }
        }
        if (cnt == 0)
            return 0;
        return max_area;
    }
};
