class Solution {
public:
    vector<int> shortestToChar(string s, char c) {
        // 
        int len = s.size();
        vector<int> ans(len, len-1);
        auto absDiff = [](int a, int b) -> int{
            if(a>b){
                return a-b;
            }
            return b-a;
        };
        for(int i = 0; i < len; i++){
            char elem = s[i];
            if(elem == c){
                ans[i] = 0;
            }else{
                for(int j = 0; j < len; j++){
                    char temp = s[j];
                    if(temp == c){
                        ans[i] = min(ans[i], absDiff(i,j));
                    }
                }    
            }
        }
        return ans;
    }
};
