            revStack.push(stack.top());
            stack.pop();
        }// stackrevstack

        int ans = revStack.top(); // revstack

        while(!revStack.empty()){
            stack.push(revStack.top());
            revStack.pop();
        }// stack
        return ans;
    }

    bool empty() {
        return stack.empty();
    }
