class Solution {
public:
    ListNode* rotateRight(ListNode* head, int k) {
        //n
        // k % nn-k%n
        if(head == nullptr){
            return nullptr;
        }

        ListNode* lastNode = head;
        int n = 1;
        while(lastNode->next!= nullptr){
            lastNode = lastNode->next;
            n++;
        }
        ListNode* node = head;
        for(int i = 1; i < n-k%n; i++){
            node = node->next;
        }
        if(node->next == nullptr){
            //
            return head;
        }else{
            ListNode* newHead = node->next;
            node->next = nullptr;
            lastNode->next = head;
            return newHead;
        }

    }
};
