class Solution {
public:
    ListNode *getIntersectionNode(ListNode *headA, ListNode *headB) {
        // 1list Alist B  

        // 

        ListNode* ptrA = headA;
        ListNode* ptrB = headB;

        // A B mAnB
        int m = 1, n = 1; 

        while(ptrA->next!=nullptr){
            ptrA = ptrA->next;
            m++;
        }
        while(ptrB->next!=nullptr){
            ptrB = ptrB->next;
            n++;
        }

        // abs(m-n)
        int distance = abs(m-n);
        ptrA =headA;
        ptrB = headB;
        if(m>n){
            while(distance>0){
                ptrA = ptrA->next;
                distance--;
            }
        }else{
            while(distance>0){
                ptrB = ptrB->next;
                distance--;
            }
        }


        if(ptrA == ptrB){
            return ptrA;// 
        }
        ListNode* ans = nullptr;
        while(ptrA->next!=nullptr){
            if(ptrA->next == ptrB->next){
                ans = ptrA->next;
                break;
            }
            ptrA = ptrA->next;
            ptrB = ptrB->next;
        }
        return ans;
    }
};
