class Solution {
public:
    ListNode *detectCycle(ListNode *head) {
        // 
        // headslow
        if(head == nullptr || head->next == nullptr){
            return nullptr;
        }
        ListNode* fast = head;
        ListNode* slow = head;
        int flag = 0;
        while(fast!=nullptr && fast->next != nullptr){
            fast = fast->next->next;
            slow = slow->next;
            if(fast == slow){
                flag = 1;
                break;
            }
        }
        if(flag == 0){
            return nullptr;
        }
        // flag=12sss
        // headxs-x,s-(s-x)=x
        // head=x=
        ListNode* ptr = head;
        while(1){
            if(ptr == slow){
                break;
            }
            ptr = ptr->next;
            slow = slow->next;
        }
        return ptr;
    }
};
