// Encodes a tree to a single string.
    string serialize(TreeNode* root) {
        if(root == nullptr){
            return "null";
        }
        vector<std::optional<int>> v;
        queue<TreeNode*> q;
        q.push(root);
        TreeNode* node; // current node
        while(!q.empty()){
            node = q.front();
            q.pop();
            if(node == nullptr){
                v.push_back(std::nullopt);
            }else{
                v.push_back(node->val);
                q.push(node->left);
                q.push(node->right);
            }
        }
        //Remove trailing None values to minimize data size
        // nullnullleetcode~

        return intVectorToString(v);
    }

    // Decodes your encoded data to tree.
    TreeNode* deserialize(string data) {
        if(data == "null"){
            return nullptr;
        }
        vector<std::optional<int>> v = stringToIntVector(data);

        queue<TreeNode*> q;
        TreeNode* root = new TreeNode(v.at(0).value());
        q.push(root);
        TreeNode* node; // current node
        int i = 1;
        while(!q.empty() && i < v.size()){
            node = q.front();
            q.pop();
            if(v.at(i).has_value()){
                node->left = new TreeNode(v.at(i).value());
                q.push(node->left);
            }
            i++;
            if(v.at(i).has_value() && i < v.size()){ // 
                node->right = new TreeNode(v.at(i).value());
                q.push(node->right);
            }
            i++;
        }
        return root;
    }
};

