class Solution {
    public ListNode rotateRight(ListNode head, int k) {
        if (head == null) {
            return head;
        }

        ListNode end = head;
        int len = 1;

        while (end.next != null) {
            end = end.next;
            len += 1;
        }
        // return new ListNode(len);
        // end  len
        // 

        end.next = head;
        // kklenlen
        while (k >= len) {
            k -= len;
        }
        if (k == 0) {
            // 
            end.next = null;
        } else {
            //  len-k 
            ListNode pre = head;
            for (int i = 0; i < len - k - 1; i++) {
                pre = pre.next;
            }
            // pre.next 
            head = pre.next;
            // 
            pre.next = null;

        }

        return head;

    }
}
