class Solution {
    public ListNode swapPairs(ListNode head) {
        // 
        if(head == null || head.next == null){
            return head;
        }

        ListNode temph = new ListNode(0);
        temph.next = head;
        ListNode slow = temph;
        ListNode fast = temph.next.next;

        while(fast.next != null && fast.next.next != null){
            ListNode temp = fast.next;
            fast.next = slow.next;
            slow.next = fast;
            fast = fast.next;
            fast.next = temp;

            slow = slow.next.next;
            fast = fast.next.next;
        }
        if(fast.next == null){
            fast.next = slow.next;
            slow.next = fast;
            fast = fast.next;
            fast.next = null;
        }
        else if(fast.next.next == null){
            ListNode temp = fast.next;
            fast.next = slow.next;
            slow.next = fast;
            fast = fast.next;
            fast.next = temp;
        }
        return temph.next;
    }
}
