        int ans = 0, n = nums.size() + 1;
        vector<int> sum(n, 0);
        unordered_map<int, int> mp;
        for (int i = 1; i < n; i++) {
            sum[i] = sum[i - 1] + nums[i - 1];
            int mod = (sum[i] % k + k) % k;
            mp[mod]++;
        }

        for (auto &[k, v] : mp) {
            if (!k) ans += v;
            ans += v * (v - 1) / 2;
        }

