class Solution {
public:
    int smallestDistancePair(vector<int>& nums, int k) {
        sort(nums.begin(), nums.end());
        int n=nums.size(), l=0, r=nums[n-1]-nums[0];
        while(l <= r){
            int m=(l+r)/2, cnt=0;
            for(int i=0,j=0;j<n;j++){
                while(nums[j]-nums[i] > m)
                    i++;
                cnt += j-i;
            }
            if(cnt >= k)
                r = m-1;
            else
                l = m+1;
        }
        return l;
    }
