class Solution {
    public int maxVowels(String s, int k) {
        int cnt = 0;
        int left = 0;
        int right = 0;
        while(right < s.length() && right < k){
            cnt += isNeed(s.charAt(right)) ? 1 : 0;
            right++;
        }
        int max = cnt;
        while(right < s.length()){
            cnt -= isNeed(s.charAt(left)) ? 1 : 0;
            cnt += isNeed(s.charAt(right)) ? 1 : 0;
            right++;
            left++;
            max = Math.max(max, cnt);
        }
        return max;
    }

    private boolean isNeed(char c){
