                if (isConnected[i][j]) connect(i, j);
            }
        }
        return ans;
    }
};

class Solution {
    public int minMalwareSpread(int[][] graph, int[] initial) {
        Arrays.sort(initial);
        int N = graph.length;
        int ans = initial[0];
        int max = 0;
        boolean[] init = new boolean[N];
        for (int p : initial) {
            init[p] = true;
        }
        for (int p : initial) {
            init[p] = false;
            int count = process(graph, p, new boolean[N], init);
            if (count > max) {
                max = count;
                ans = p;
            }
            init[p] = true;
        }
        return ans;
    }

    private int process(int[][] graph, int p, boolean[] visited, boolean[] initial) {
        if (initial[p]) {
            return 0;
        }
        visited[p] = true;
        int count = 1;
        for (int q = 0; q < graph[p].length; q++) {
            if (!visited[q] && graph[p][q] == 1) {
                int c = process(graph, q, visited, initial);
                if (c == 0) {
                    return 0;
                }
                count += c;
            }
        }
        return count;
    }
}

class Solution {
public:
    int makeConnected(int n, vector<vector<int>>& connections) {
        if (connections.size() < n - 1) {
            return -1;
        }

        vector<int> fa(n);
        iota(fa.begin(), fa.end(), 0);    

        function<int(int)> findset = [&](int x) {return x == fa[x] ? x : fa[x] = findset(fa[x]);};
        int part = n;
        for (auto&& c: connections) {
            int p = findset(c[0]), q = findset(c[1]);
            if (p != q) {
                --part;
                fa[p] = q;
            }
        }

        return part - 1;
    }
};

