            ans.push_back(index);            
            return {};
        }

        for(int i=ii+1;i<candidates.size();i++){
            if(target<candidates[i]){break;}
            if(i>ii+1&&candidates[i]==candidates[i-1]){
                continue;
            }
            index.push_back(candidates[i]);
            combinationSum2(candidates,target-candidates[i],i);
            index.pop_back();
        }
        return ans;
    }
};

