public ListNode detectCycle(ListNode head) {
        // fastslowfastslow
        ListNode fast = head;
        ListNode slow = head;
        while(true){
              if(fast == null || fast.next == null) return null;
              fast = fast.next.next;
              slow = slow.next;
              if(fast == slow) break;
        }

        fast = head;
        while(fast != slow){
            fast=fast.next;
            slow = slow.next;
        }
        return slow;
