class Solution {
    public int numberOfBoomerangs(int[][] points) {
        int n = points.length;
        // n^2 
        //  hashMap560 K 1248 
        int res =0;
        // A(a,b)=a!/(a-b)!,mm/(m-2)= m*(m-1)
        for(int i = 0; i < n; i++){
            HashMap<Double,Integer> map = new HashMap<>(n);
            int [] curPoint = points[i];
            for(int j = 0; j < n; j++){
                if(i != j){
                    double dis = calDistance(points[j],curPoint);
                    map.put(dis,map.getOrDefault(dis,0)+1);
                }
            }
            for(double dis:map.keySet()){
                int countDis = map.get(dis);
                res += countDis*(countDis-1);
            }
        }
        return res;
    }

    private double calDistance(int[] x,int []y) {
        return Math.pow(x[0]-y[0],2)+Math.pow(x[1]-y[1],2);
    }
