 public int minSubarray(int[] nums, int p) {
            // S[]
            // 
            // (A-B)%p=0ABA%p = B%p A<0 B>=0 A%p +p = B%p;(A%p +p)%p =B%p
            // x
            //  x%p -( s[i]%p -s[j]%p)%p=0i>=j
            //  X - SI+SJ)%p ==0  (SJ -(SI-X))%p = 0  SJ%p = ((SI-X)%p+p)%p
            //  iijj (S[ii]-X)%p+p[jj,ii)
            int n = nums.length, ans = n;
            int s[] = new int[n + 1];
            for (int i = 0; i < n; ++i){
                // 
                s[i + 1] = (s[i] + nums[i]) % p;
            }
            int x = s[n];
            if (x == 0) return 0;

            // mapkey value
            HashMap<Integer,Integer> last = new HashMap<Integer, Integer>();
            for (int i = 0; i <= n; ++i) {
                last.put(s[i], i);
                // -n  i-j >= n
                int j = last.getOrDefault((s[i] - x + p) % p, -n);
                ans = Math.min(ans, i - j);
            }
            return ans < n ? ans : -1;
        }
