class Solution {
        public boolean possibleBipartition(int n, int[][] dislikes) {
            //   UF
            //  dislikesdislikes[i][0]dislikes[i][1]unionconnected
            UF myUf = new UF(n);

            // numsvalueList,1
            List<Integer>[] nums = new List[n + 1];
            // 
            for (int i = 0; i <= n; ++i) {
                nums[i] = new ArrayList<Integer>();
            }
            for (int item[] : dislikes) {
                int a = item[0];
                int b = item[1];

                // ablist
                nums[a].add(b);
                nums[b].add(a);
            }

            for (int i = 1; i <= n; i++) {
                List<Integer> list = nums[i];
                for (int j = 0; j < list.size(); j++) {
                    // 
                    myUf.union(list.get(0), list.get(j));
                    // false
                    if (myUf.connected(list.get(j), i)) {
                        return false;
                    }

                }
            }
            return true;
        }

        class UF {
            private int count;
            private int parent[];

            public UF(int n) {
                parent = new int[n+1];
                for (int i = 0; i <= n; i++) {
                    parent[i] = i;
                }
                count = n;
            }

            public int getUFCount() {
                return count;
            }

            public int findRoot(int x) {
                // ifwhile
               /* if (parent[x] != x) {
                    parent[x] = findRoot(parent[x]);
                }
                return parent[x];*/
                while (parent[x] != x) {
                    // 
                    parent[x] = parent[parent[x]];
                    x = parent[x];
                }
                return x;
            }

            public void union(int a, int b) {
                int rootA = findRoot(a);
                int rootB = findRoot(b);
                if (rootA == rootB) {
                    return;
                }
                parent[rootA] = rootB;

                // count--
                count--;
            }

            public boolean connected(int a, int b) {
                return findRoot(a) == findRoot(b);
            }
        }
    }

