        //  +
        int n = tasks.length;
        // mix
        int mix[][] = new int[n][3];
        for(int i =0;i<n;i++){
            int[] task = tasks[i];
            mix[i] = new int[]{task[0],task[1],i};
        }
        //  
        Arrays.sort(mix,(a,b)->a[0]-b[0]);
        // +
        PriorityQueue<int[]> pq = new PriorityQueue<>((a,b)->{
            if(a[1]!=b[1]){
                return a[1]-b[1];
            }
            return a[2]-b[2];
        });

        // imixtimeindexres
        int i =0,time =1;
        int res[] = new int[n];
        for(int index =0;index<n;){
            // 
            while(i<n && mix[i][0]<= time){
                pq.add(mix[i++]);
            }
            // 
            if(pq.isEmpty()){
                time = mix[i][0];
            }else{
                int[] cur = pq.poll();
                res[index++] = cur[2];
                // 
                time+= cur[1];
            }

        }
        return res;
    }
