            int y = text2.length();
            // dp[i][j]text1[0i]text2[0j]
            // dp[0][0]dp[i][0]dp[0][j]0dp[x][y]+1
            int dp[][] = new int[x + 1][y + 1];

            // i j11dp
            for (int i = 1; i <= x; i++) {
                char c = text1.charAt(i-1);
                for (int j = 1; j <= y; j++) {
                    char c1 = text2.charAt(j-1);
                    if (c == c1) {
                        dp[i][j] = dp[i - 1][j - 1] + 1;
                    } else {
                        dp[i][j] = Math.max(dp[i - 1][j], dp[i][j - 1]);
                    }
                }
            }
            return dp[x][y];
        }
    }

