        int memo[][];


        private int traverse(int start, int end) {
            if (start > end) {
                return 1;
            }
            if(memo[start][end] != 0){
                return memo[start][end];
            }
            int res = 0;
            for (int i = start; i <= end; i++) {
                // iroot
                int leftCount = traverse(start, i - 1);
                int rightCount = traverse(i + 1, end);

                res += leftCount * rightCount;
            }
            memo[start][end] = res;
            return res;
        }
