            return []
        p_arr = [0]*26
        for ch in p:
            # print(ch,ord(ch)-ord('a'),ord('y'),ord(ch))
            p_arr[ord(ch)-ord('a')]+=1
        s_arr = [0]*26
        for ch in s[:len(p)]:
