class Solution:
    def minWindow(self, s: str, t: str) -> str:
        t_arr = collections.defaultdict(int)
        for ch in t:
            t_arr[ch]+=1
        def ifcover(cur_arr):
            for k in t_arr:
                if cur_arr[k]<t_arr[k]:
                    return False
            return True
        cur_arr = collections.defaultdict(int)
        rs = ""
        start=0
        end=start+1
        while start<len(s):
            while end<=len(s):
                cur_arr[s[end-1]]+=1
                if ifcover(cur_arr):
                    break
                end+=1
            if not ifcover(cur_arr):
                return rs
            while start<end and cur_arr[s[start]]-1>=t_arr[s[start]]:
                cur_arr[s[start]]-=1
                start+=1
            if rs=="" or end-start<len(rs):
                rs = s[start:end]
            cur_arr[s[start]]-=1
            start+=1
            end+=1
        return rs

