class Solution:
    def knightProbability(self, n: int, k: int, row: int, column: int) -> float:
        def check(x,y):
            if x>=0 and x<n and y>=0 and y<n:
                return True
            else:
                return False
        @lru_cache
        def step(x,y):
            rs = []
            if check(x-2,y-1):
                rs.append((x-2,y-1))
            if check(x-1,y-2):
                rs.append((x-1,y-2))
            if check(x+1,y-2):
                rs.append((x+1,y-2))
            if check(x+2,y-1):
                rs.append((x+2,y-1))
            if check(x+2,y+1):
                rs.append((x+2,y+1))
            if check(x+1,y+2):
                rs.append((x+1,y+2))
            if check(x-2,y+1):
                rs.append((x-2,y+1))
            if check(x-1,y+2):
                rs.append((x-1,y+2))
            return rs
        queue = collections.defaultdict(int)
        queue[(row, column)] = 1
        for i in range(k):
            # print(queue)
            if not queue:
                return 0
            tmp = collections.defaultdict(int)
            for (x,y),nums in queue.items():
                next_list = step(x,y)
                for nl in next_list:
                    tmp[nl]+=nums
            queue = tmp
        return sum(list(queue.values()))/8**k
