            return self.stack1.pop()
        else:
            while not self.stack2.empty():
                self.stack1.push(self.stack2.pop())
            return self.stack1.pop()

    def peek(self) -> int:
        if not self.stack1.empty():
            return self.stack1.peek()
        else:
            while not self.stack2.empty():
                self.stack1.push(self.stack2.pop())
            return self.stack1.peek()

    def empty(self) -> bool:
        return self.stack1.empty() and self.stack2.empty()

