class Solution:
    def sortItems(self, n: int, m: int, group: List[int], beforeItems: List[List[int]]) -> List[int]:
        # 
        def tp_sort(gids,inner_p_map):
            res = []
            igs = {}
            for g in gids:
                igs[g]=0
            for g in gids:
                if g in inner_p_map:
                    cs = inner_p_map[g]
                    for ccs in cs:
                        igs[ccs] += 1
            while len(res) != len(gids):
                cur = []
                for g in  gids:
                    if g in igs and  igs[g]==0:
                        cur.append(g)
                        del(igs[g])
                        if g in inner_p_map:
                            cs = inner_p_map[g]
                            for ccs in cs:
                                igs[ccs]-=1
                if len(res) != len(gids) and not cur:return []
                res.extend(cur)

            return res

        res = []
        groups = set()
        inf = -100000
        group_input_degree = {} # key val-1-1*0-1-100000
        group_mem_id = {} # keyvalidlist
        p_map = {} # 
        inner_p_map = {} # 
        for i,x in enumerate(group):
            if x == -1:
                bis = beforeItems[i]
                gid = -1 * i if i !=0 else inf
                group_input_degree[gid] = len(bis)
                for bi in bis:
                    if bi not in p_map:
                        p_map[bi] = set()
                    p_map[bi].add(gid)
            else:
                gid = x 
                if gid not in group_mem_id:
                    group_mem_id[gid] = set()
                group_mem_id[gid].add(i)
            groups.add(gid)
        for gid in group_mem_id:
            if gid < 0 :continue
            gms = group_mem_id[gid]
            for g in gms:
                par = beforeItems[g]
                if par:
                    for p in par:
                        if p in gms:
                            if p not in inner_p_map:
                                inner_p_map[p]=set()
                            inner_p_map[p].add(g)
                        else:
                            if p not in p_map:
                                p_map[p]=set()
                            p_map[p].add(gid)
        # 
        for g in groups:
            group_input_degree[g]=0
        for p,gg in p_map.items():
            for g in gg:
                group_input_degree[g] += 1
        while group_input_degree:
            cur = []
            used = set()
            for g in group_input_degree:
                if group_input_degree[g]==0:
                    if g < 0:
                        it = -1 * g if g != inf else 0
                        res.append(it)
                        if it in p_map:
                            cur.extend(list(p_map[it]))
                    else:
                        gids = group_mem_id[g]
                        tmp = tp_sort(gids,inner_p_map)
                        if not tmp:return []
                        res.extend(tmp)
                        for it in group_mem_id[g]:
                            if it in p_map:
                                cur.extend(list(p_map[it]))
                    used.add(g)
            for u in used:
                del(group_input_degree[u])
            if group_input_degree and not cur:return []
            for cc in cur:
                group_input_degree[cc]-=1
        return res
