    func search(_ word: String) -> Bool {
        var currentNode = root
        for char in word {
            if currentNode.children[char] == nil {
                return false
            }
            currentNode = currentNode.children[char]!
        }
        return currentNode.isEndOfWord
    }

    func startsWith(_ prefix: String) -> Bool {
        var currentNode = root
        for char in prefix {
            if currentNode.children[char] == nil {
                return false
            }
            currentNode = currentNode.children[char]!
        }
        return true
