var sortedListToBST = function(head) {
    if (!head) {
        return null;
    }

    let slow = head;
    let fast = head;
    let preSlow; //slow

    while(fast && fast.next) {
        preSlow = slow;
        slow = slow.next;
        fast = fast.next.next;
    }

    const root = new TreeNode(slow.val);
    if (preSlow) {
        preSlow.next = null;
        root.left = sortedListToBST(head)
    }

    root.right = sortedListToBST(slow.next)

    return root
};

