var findTargetSumWays = function(nums, target) {
    // 
    let sum = 0;
    for (const num of nums) {
        sum += num;
    }

    //  P
    const diff = sum - target;
    if (diff < 0 || diff % 2 !== 0) {
        //  diff  0
        return 0;
    }

    const neg = Math.floor(diff / 2);

    //  dp 
    const dp = new Array(neg + 1).fill(0);
    dp[0] = 1;  // 0

    //  dp 
    for (const num of nums) {
        for (let j = neg; j >= num; j--) {
            dp[j] += dp[j - num];
        }
    }

    //  neg 
    return dp[neg];
};
