var MapSum = function() {
    this.map = new Map()
};

/** 
 * @param {string} key 
 * @param {number} val
 * @return {void}
 */
MapSum.prototype.insert = function(key, val) {
    this.map.set(key, val)
};

/** 
 * @param {string} prefix
 * @return {number}
 */
MapSum.prototype.sum = function(prefix) {
    let res = 0;
    for(const s of this.map.keys()) {
        if (s.startsWith(prefix)) {
            res += this.map.get(s)
        }
    }
    return res
};

sum: O(n)
insert: O(n)
