class Solution {
    public int reversePairs(int[] nums) {
        return helper(nums, 0, nums.length - 1);
    }

    private int helper(int[] nums, int start, int end) {
        if (start >= end) {
            return 0;
        }

        int mid = start + (end - start) / 2;
        int res = helper(nums, start, mid) + helper(nums, mid + 1, end) + count(nums, start, mid, end);
        int p1 = start, p2 = mid + 1, n = end - start + 1;
        int[] arr = new int[n];

        for (int i = 0; i < n; ++i) {
            if (p1 > mid || p2 <= end && nums[p2] < nums[p1]) {
                arr[i] = nums[p2];
                ++p2;
            } else {
                arr[i] = nums[p1];
                ++p1;
            }
        }

        for (int i = start; i <= end; ++i) {
            nums[i] = arr[i - start];
        }

        return res;
    }

    private int count(int[] nums, int start, int mid, int end) {
        int p1 = start, p2 = mid + 1, cnt = 0;
        while (p1 <= mid && p2 <= end) {
            if (nums[p1] * 1l > 2l * nums[p2]) {
                cnt += (mid - p1 + 1);
                ++p2;
            } else {
                ++p1;
            }
        }
        return cnt;
    }
}
```

