// 查找 nums[i] > target 的个数
var getNums = function(subNums, target) {
    var left = 0;
    var right = subNums.length - 1;
    while(left <= right) {
        var mid = Math.floor(left + (right - left) / 2);
        if (subNums[mid] <= target ) {
            left = mid + 1;
        }
        else if (subNums[mid] > target) {
            right = mid - 1;
        }
    }
    return subNums.length - left;
};

// 二分查找
function binarySearch(sortedArray, target) {
    let left = 0;
    let right = sortedArray.length - 1;
    while (left <= right) {
        var mid = Math.floor(left + (right - left) / 2);
        if (sortedArray[mid] === target) {
            return mid;
        } else if (sortedArray[mid] < target) {
            left = mid + 1;
        } else {
            right = mid - 1;
        }
    }
    return left;
}

// 二分法插入，保证已遍历数据
function insertWithBinarySearch(sortedArray, newData) {
    let insertIndex = binarySearch(sortedArray, newData);
    sortedArray.splice(insertIndex, 0, newData);
    return sortedArray;
}
/**
 * @param {number[]} nums
 * @return {number}
 */
// 一边遍历一边维护一个有序序列 d，其中 d 为已经遍历过的值的集合
var reversePairs = function(nums) {
    var len = nums.length;
    if (len <=1) return 0;
    var sortedNums = [nums[0]];
    var count = 0;
    for(i = 1; i < len; i++) {
        // 对于每一个位置 0 <= i < n，我们统计 d 中大于 2 * A[i] 的个数
        count += getNums(sortedNums, nums[i] * 2);
        // 二分法插入，保证已遍历数据
        insertWithBinarySearch(sortedNums, nums[i]);
    };
    return count;
};
