```js
/**
 * @param {number[]} houses
 * @param {number[]} heaters
 * @return {number}
 */
const binarySearchRight = (nums, target) => {
    let left = 0;
    let right = nums.length - 1;
    let ans = -1;
    while (left <= right) {
        let mid = Math.floor(left + (right - left) / 2);
        if (nums[mid] <= target) {
            ans = mid;
            // 搜索区间变为 [mid+1, right]
            left = mid + 1;
        }
        if (nums[mid] > target) {
            // 搜索区间变为 [left, mid-1]
            right = mid - 1;
        }
    }
    return ans;
};

const findRadius = function(houses, heaters) {
    let minRadius = 0;
    let heatersLen = heaters.length;
    heaters.sort((a, b) => a - b);
    // 遍历每个房屋，去寻找2个离房屋最近的暖气。 
    // ① 暖气1下标为i(heaters[i]≤house) ② 暖气2坐标为j(j=i+1)
    for(let house of houses) {
        let i = binarySearchRight(heaters, house);
        let j = i + 1;
        // i < 0 房子左边无暖气
        const leftDistance = i < 0 ? Number.MAX_VALUE : house - heaters[i];
        // j >= heatersLen 房子右边无暖气
        const rightDistance = j >= heatersLen ? Number.MAX_VALUE : heaters[j] - house;

        const curDistance = Math.min(leftDistance, rightDistance);
        minRadius = Math.max(minRadius, curDistance);
    }
    return minRadius;
};
```

