/**
 * @param {ListNode} head
 * @return {ListNode}
 */
var middleNode = function(head) {
    let slow = head;
    let fast = head;

    // 当 fast 指针到达末尾时，slow 指针会指向中间结点
    while (fast !== null && fast.next !== null) {
        slow = slow.next;
        fast = fast.next.next;
