/**
 * Definition for singly-linked list.
 * public class ListNode {
 *     int val;
 *     ListNode next;
 *     ListNode() {}
 *     ListNode(int val) { this.val = val; }
 *     ListNode(int val, ListNode next) { this.val = val; this.next = next; }
 * }
 */
class Solution {
    public ListNode rotateRight(ListNode head, int k) {
        if (head == null || k == 0) {
            return head;
        }
        ListNode p = new ListNode();
        p = head; 
        int tot = 1;
        //找到链表中最后一个节点
        while (p.next != null) {
            p = p.next;
            tot++;
        }
        // 有可能k的大小超过数组的长度
        k %= tot;
        ListNode w = new ListNode();
        w = head; 
        // 计算要走到哪个节点位置上 
        for (int i = 0; i < tot - k - 1; i++) {
            w = w.next;
        }
        p.next = head;
        head = w.next;
        w.next = null;
        return head;
    }
}
```
