class Solution {
    public ListNode swapPairs(ListNode head) {
        if (head == null) return head;
        ListNode dummy = new ListNode();
        ListNode p = new ListNode();
        ListNode q = new ListNode();
        ListNode w = new ListNode();
        p = head;
        q = head.next;
        dummy.next = head;
        w = dummy;
        while (p != null && q != null) {
            w.next = q;
            p.next = q.next;
            q.next = p;
            w = p;
            p = p.next;
            if (p != null) {
                q = p.next;
            }
        } 
        return dummy.next;
    }
} 
