    vector<int> topKFrequent(vector<int>& nums, int k) {
        unordered_map<int, int> cnt;
        for (int val : nums) cnt[val]++;
        int n = nums.size();
        vector<int> ss(n+1);
        for (auto it = cnt.begin(); it != cnt.end(); it++) {
            ss[it->second]++;
        }
        int index = n;
        while (k > 0) {
            k -= ss[index--];
        }
        vector<int> ans;
        for (auto it = cnt.begin(); it != cnt.end(); it++) {
            if (it->second > index) {
                ans.push_back(it->first);
            }
        }
        return ans;
    }
