class Solution {
public:
    vector<int> findSubstring(string s, vector<string>& words) {
        vector<int> ans;
        if (words.empty()) return ans;
        int n = s.size(), m = words.size(), sz = words[0].size();
        unordered_map<string, int> w_map;
        for (string element : words) w_map[element] ++; 

        for (int i = 0; i < sz; i++) { 
            unordered_map<string, int> s_map;
            int cnt = 0; //有效单词的数量
            for (int j = i; j + sz <= n; j += sz) {
               if (j >= i + m * sz) {
                   auto str = s.substr(j - m * sz, sz);
                   s_map[str] --;
                   if (s_map[str] < w_map[str]) cnt--;
               }
                auto new_str = s.substr(j, sz);
                s_map[new_str] ++;
                if (s_map[new_str] <= w_map[new_str]) cnt++;
                if (cnt == m) ans.push_back(j - sz * (m-1));
            }
        }
        return ans;
    }
};


