class Solution:
    def sortedListToBST(self, head: Optional[ListNode]) -> Optional[TreeNode]:
        if not head or not head.next:
            return head
        p = head
        lst = []
        while p:
            lst.append(p.val)
            p = p.next

        mid = len(lst) // 2
        root = self.treeHelper(lst, mid, 0, len(lst)-1)
        return root

    def treeHelper(self, lst, mid, left, right):
        if left == right:
            return TreeNode(lst[left], None, None)
        elif left > right:
            return None
        root = TreeNode(lst[mid])
        root.left = self.treeHelper(lst, (left+mid-1)//2, left, mid-1)
        root.right = self.treeHelper(lst, (mid+1+right)//2, mid+1, right)
        return root
