import collections
class Solution:
    def verticalTraversal(self, root: Optional[TreeNode]) -> List[List[int]]:
        # first, level order traversal to have x index sorted in a dict e.g. {-1:[9] 0:[3, 15], 1:[20]...}
        d = collections.defaultdict(list)
        q = collections.deque([(root, 0)])
        while q:
            l = len(q)
            tmp = collections.defaultdict(list)
            for i in range(l):
                node, y = q.popleft()
                tmp[y].append(node.val)
                if node.left:
                    q.append((node.left, y-1))
                if node.right:
                    q.append([node.right, y+1])
            # sort the list in the same level to make sure the node with same index is ordered by values
            for y in tmp:
                d[y].extend(sorted(tmp[y]))

        return [d[key] for key in sorted(d)]
