            ctsub = collections.defaultdict(int)
            for p in range(0, len(subs)-wl+1, wl):
                ctsub[subs[p: p+wl]] += 1

            if len(ctsub) != len(ct):
                return False

            for key in ct:
                if key not in ctsub or ctsub[key] != ct[key]:
                    return False

            return True


