class Solution:
    def sortArray(self, nums: List[int]) -> List[int]:
        return self.helper(nums, 0, len(nums)-1)

    def helper(self, nums, left, right):
        if left == right:
            return [nums[left]]
        mid = (left + right) // 2
        left_sort = self.helper(nums, left, mid)
        right_sort = self.helper(nums, mid+1, right)
        res = []
        i, j = 0, 0
        while i < len(left_sort) and j < len(right_sort):
            if left_sort[i] < right_sort[j]:
                res.append(left_sort[i])
                i += 1
            else:
                res.append(right_sort[j])
                j += 1
        while i < len(left_sort):
            res.append(left_sort[i])
            i += 1
        while j < len(right_sort):
            res.append(right_sort[j])
            j += 1
        return res
