class Solution:
    '''
    edge case: len(nums) == 1, return 0

    nums [1,3,2,3,1]

    2*nums [2,6,4,6,2]

    O(n^2)


    '''
    def reversePairs(self, nums: List[int]) -> int:
        if len(nums) == 1:
            return 0

        self.count = 0

        def mergeSort(nums, lo, hi):
            if lo < hi:
                mid = (lo + hi) // 2
                mergeSort(nums, lo, mid)
                mergeSort(nums, mid+1, hi)
                merge(nums, lo, mid, hi)


        def merge(nums, lo, mid , hi):
            # left: lo ... mid
            # right: mid+1 ... hi
            i, j = lo, mid+1
            while i<=mid and j<=hi:
                if nums[i] <= 2*nums[j]:
                    i += 1
                else:
                    self.count += mid-i+1
                    j += 1

            temp = []
            i, j = lo, mid+1
            while i<=mid and j<=hi:
                if nums[i]<=nums[j]:
                    temp.append(nums[i])
                    i += 1
                else:
                    temp.append(nums[j])
                    j += 1

            while i<=mid:
                temp.append(nums[i])
                i += 1
            while j<=hi:
                temp.append(nums[j])
                j += 1
            for i in range(len(temp)):
                nums[lo+i] = temp[i]


        mergeSort(nums, 0, len(nums)-1)

        return self.count

