class Solution:
    def smallestDistancePair(self, nums: List[int], k: int) -> int:
        def get_count(dis):
            cnt = l = 0
            for r in range(len(nums)):
                while nums[r] - nums[l] > dis:
                    l += 1
                cnt += r - l
            return cnt

        nums.sort()
        left, right = 0, nums[-1] - nums[0]
        while left <= right:
            mid = (left + right) // 2
            if get_count(mid) < k:
                left = mid + 1
            else:
                right = mid - 1
        return left
