```class Solution {
    public int[] shortestToChar(String s, char c) {
        int[] result = new int[s.length()];
        int len = s.length();
        int latestC = len;
        for (int i = 0; i < len; i++) {
            char tmp = s.charAt(i);
            if (tmp == c) {
                result[i] = 0;
                latestC = i;
                continue;
            }
            result[i] = Math.abs(latestC - i);
        }
        latestC = 0;
        for (int i = len - 1; i >= 0; i--) {
            char tmp = s.charAt(i);
            if (tmp == c){
                result[i] = 0;
                latestC = i;
                continue;
            }
            result[i] = Math.min(result[i], Math.abs(i - latestC));
        }
        return result;
    }
