class Solution {
    public List<Integer> addToArrayForm(int[] num, int k) {
        int n = num.length;
        int carry = 0;
        List<Integer> ans = new ArrayList<>(); // SC O(max(n, log k))
        for (int i=n-1; i>=0 || k > 0; i--) { // TC O(max(n, log k))
            int sum = (i >= 0 ? num[i] : 0) + k % 10 + carry; // 当前位
            carry = sum / 10; // 进位
            ans.add(sum % 10); // 记录结果

            k /= 10; // k向左移
        }

        if (carry == 1) {
            ans.add(1); // 最后如果有进位
        }

        Collections.reverse(ans); // TC O(max(n, log k))
